import React, { useState, useEffect } from "react";
import NewTask from "../components/NewTask";
import Task from "../components/Task";
import { taskFindAll } from "../helpers/task";

const AllTasks = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const setup = async () => {
      const tasksData = await taskFindAll();
      if (tasksData) {
        setTasks(tasksData);
      }
    };
    setup();
  }, []);

  const onChange = () => {
    const tasksData = taskFindAll();
    if (tasksData) {
      setTasks(tasksData);
    }
  };
  return (
    <div>
      {tasks.map((task) => (
        <Task allTasks={tasks} task={task} onChange={onChange} key={task.id} />
      ))}
      <NewTask onChange={onChange} />
    </div>
  );
};

export default AllTasks;
