import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSquare, faCheckSquare } from "@fortawesome/free-regular-svg-icons";

const task = ({ task }) => {
  return (
    <div
      className={
        task.completed_on
          ? "row xs-between completed-task"
          : "row xs-between task"
      }
    >
      <div className="col-xs-1 check">
        <FontAwesomeIcon
          icon={task.completed_on ? faCheckSquare : faSquare}
          size="2x"
        />
      </div>
      <div className="col-xs-11 details">
        <h3>
          {task.id}. {task.description}
        </h3>
      </div>
    </div>
  );
};

export default task;
