import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSquare, faCheckSquare } from "@fortawesome/free-regular-svg-icons";
import { taskToggleStatus } from "../helpers/task";

const Task = ({ task, onChange }) => {
  const [currentTask, setCurrentTask] = useState(task);

  return (
    <div
      className={
        currentTask.completed === 1
          ? "row xs-between completed-task"
          : "row xs-between task"
      }
    >
      <div
        className="col-xs-4 col-sm-2 col-md-1 check"
        onClick={() => {
          let newTask = {
            id: task.id,
            description: task.description,
            completed: task.completed ? 0 : 1,
          };

          taskToggleStatus(currentTask);
          setCurrentTask(newTask);
          onChange();
        }}
      >
        <FontAwesomeIcon
          className="icon"
          icon={currentTask.completed === 1 ? faCheckSquare : faSquare}
          size="2x"
        />
      </div>
      <div className="col-xs-8 col-sm-10 col-md-11 details">
        <h3>
          {currentTask.id}. {currentTask.description}
        </h3>
      </div>
    </div>
  );
};

export default Task;
